    margin-bottom: 0.75rem;
}

.rule-item i {
    width: 1.25rem;
    text-align: center;
}

/* Help Page Styles */
.rank-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 1rem;
}

.rank-item {
    display: flex;
    align-items: center;
    gap: 1rem;
