.rank-badge.grinder { background: #17a2b8; color: white; }
.rank-badge.executor { background: #ffc107; color: black; }
.rank-badge.obsessor { background: #fd7e14; color: white; }
.rank-badge.disciplinar { background: #dc3545; color: white; }
.rank-badge.sentinel { background: #6f42c1; color: white; }
.rank-badge.dominus { background: #e83e8c; color: white; }
.rank-badge.phantom { background: #495057; color: white; }
.rank-badge.apex-mind { background: #00ff88; color: black; }
.rank-badge.system-override { background: #ff0080; color: white; }
.rank-badge.darkensul-core { background: linear-gradient(45deg, #ff0080, #00ff88); color: white; }

.rank-info h6 {
