.info-item {
    display: flex;
    align-items: flex-start;
    gap: 0.75rem;
    margin-bottom: 1rem;
}

.info-item i {
    margin-top: 0.25rem;
    width: 1.25rem;
    text-align: center;
    flex-shrink: 0;
}

.step-card {
    text-align: center;
    padding: 1.5rem;
    background: var(--bg-tertiary);
    border: 1px solid var(--border-color);
    border-radius: 0.75rem;
    height: 100%;
}

.step-number {
    width: 40px;
    height: 40px;
    background: var(--accent-primary);
    color: var(--bg-primary);
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 700;
    margin: 0 auto 1rem;
